import React, { useState } from "react";
import { 
  View, 
  Text, 
  TouchableOpacity, 
  Image, 
  StyleSheet,
  Platform
} from "react-native";
import WebCameraCapture from "./WebCameraCapture";

const PriceTagCapture: React.FC = () => {
  const [image, setImage] = useState<string | null>(null);

  const handleCapture = (capturedImage: string) => {
    setImage(capturedImage);
  };

  const takePicture = () => {
    // For web, this will be handled by WebCameraCapture
    console.log("Take picture clicked");
  };

  const pickImage = () => {
    // Placeholder for future image pick functionality
    console.log("Pick image clicked");
  };

  return (
    <View style={styles.container}>
      {!image ? (
        Platform.OS === 'web' ? (
          <WebCameraCapture onCapture={handleCapture} />
        ) : (
          <View style={styles.cameraPlaceholder}>
            <Text>Camera Preview (Mobile)</Text>
            <TouchableOpacity
              style={styles.button}
              onPress={takePicture}
            >
              <Text style={styles.buttonText}>Capture</Text>
            </TouchableOpacity>
          </View>
        )
      ) : (
        <View style={styles.imageContainer}>
          <Image 
            source={{ uri: image }} 
            style={styles.image} 
          />
          <View style={styles.buttonContainer}>
            <TouchableOpacity 
              style={styles.button}
              onPress={() => setImage(null)}
            >
              <Text style={styles.buttonText}>Retake</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={styles.button}
              onPress={pickImage}
            >
              <Text style={styles.buttonText}>Gallery</Text>
            </TouchableOpacity>
          </View>
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  cameraPlaceholder: {
    width: "100%",
    height: "80%",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#f0f0f0",
  },
  imageContainer: {
    width: "100%",
    height: "100%",
    justifyContent: "center",
    alignItems: "center",
  },
  image: {
    width: "90%",
    height: "80%",
    resizeMode: "contain",
  },
  buttonContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    width: "100%",
    padding: 20,
  },
  button: {
    backgroundColor: "#2196F3",
    padding: 10,
    borderRadius: 5,
  },
  buttonText: {
    color: "white",
    fontWeight: "bold",
  }
});

export default PriceTagCapture;
