import React, { useState, useRef } from "react";
import { 
  View, 
  Text, 
  TouchableOpacity, 
  Image, 
  StyleSheet 
} from "react-native";
import Webcam from "react-webcam";
import * as ImagePicker from "expo-image-picker";
import axios from "axios";
import { AlertNotificationRoot, Toast, ALERT_TYPE } from "react-native-alert-notification";

interface OCRResponse {
  productName: string;
  success: boolean;
  action: "updated" | "created";
  brand?: string;
  price?: number;
}

const PriceTagCapture: React.FC = () => {
  const [image, setImage] = useState<string | null>(null);
  const webcamRef = useRef<Webcam | null>(null);

  const takePicture = async () => {
    if (webcamRef.current) {
      const screenshot = webcamRef.current.getScreenshot();
      if (screenshot) {
        setImage(screenshot);
        // Convert base64 by removing the data URL prefix
        const base64Image = screenshot.split(",")[1];
        uploadImage(base64Image);
      }
    }
  };

  const pickImage = async () => {
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      base64: true,
      quality: 0.7
    });

    if (!result.canceled && result.assets[0].base64) {
      setImage(result.assets[0].uri);
      uploadImage(result.assets[0].base64);
    }
  };

  const uploadImage = async (base64Image?: string) => {
    if (!base64Image) return;

    try {
      const response = await axios.post<OCRResponse>("http://localhost:8080/api/price-tag/ocr", {
        imageBase64: base64Image,
        storeId: "your-store-id-here"
      });

      console.log("Upload response:", response.data);

      Toast.show({
        type: ALERT_TYPE.SUCCESS,
        title: "Success",
        textBody: `Product ${response.data.productName} added/updated`
      });
    } catch (error) {
      console.error("Image upload error:", error);

      Toast.show({
        type: ALERT_TYPE.DANGER,
        title: "Error",
        textBody: "Could not process the price tag. Please try again."
      });
    }
  };

  const renderCamera = () => {
    return (
      <View style={styles.camera}>
        <Webcam
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          style={{ width: "100%", height: "100%" }}
        />
        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={styles.button}
            onPress={takePicture}
          >
            <Text style={styles.buttonText}>Capture</Text>
          </TouchableOpacity>
        </View>
      </View>
    );
  };

  return (
    <AlertNotificationRoot>
      <View style={styles.container}>
        {!image ? (
          renderCamera()
        ) : (
          <View style={styles.imageContainer}>
            <Image 
              source={{ uri: image }} 
              style={styles.image} 
            />
            <View style={styles.buttonContainer}>
              <TouchableOpacity 
                style={styles.button}
                onPress={() => setImage(null)}
              >
                <Text style={styles.buttonText}>Retake</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={styles.button}
                onPress={pickImage}
              >
                <Text style={styles.buttonText}>Gallery</Text>
              </TouchableOpacity>
            </View>
          </View>
        )}
      </View>
    </AlertNotificationRoot>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  camera: {
    width: "100%",
    height: "80%",
    justifyContent: "flex-end",
    alignItems: "center",
  },
  imageContainer: {
    width: "100%",
    height: "100%",
    justifyContent: "center",
    alignItems: "center",
  },
  image: {
    width: "90%",
    height: "80%",
    resizeMode: "contain",
  },
  buttonContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    width: "100%",
    padding: 20,
  },
  button: {
    backgroundColor: "#2196F3",
    padding: 10,
    borderRadius: 5,
  },
  buttonText: {
    color: "white",
    fontWeight: "bold",
  }
});

export default PriceTagCapture;
