import React, { useRef, useState, useEffect } from 'react';
import { View, Animated, StyleSheet, Dimensions, Text, Platform } from 'react-native';
import Webcam from 'react-webcam';

interface WebCameraCaptureProps {
  onCapture: (image: string) => void;
}

const WebCameraCapture: React.FC<WebCameraCaptureProps> = ({ onCapture }) => {
  const webcamRef = useRef<Webcam>(null);
  const containerRef = useRef<View>(null);
  const flashOpacity = useRef(new Animated.Value(0)).current;
  const [tapCount, setTapCount] = useState(0);

  useEffect(() => {
    const container = containerRef.current;

    const handleClick = (event: MouseEvent) => {
      event.preventDefault();

      console.log('Capture image function called');
      setTapCount(prev => prev + 1);

      if (webcamRef.current) {
        console.log('Webcam ref exists');
        const imageSrc = webcamRef.current.getScreenshot();
        console.log('Screenshot:', !!imageSrc);

        if (imageSrc) {
          console.log('Image source exists');
          // Trigger flash animation
          Animated.sequence([
            Animated.timing(flashOpacity, {
              toValue: 0.7,
              duration: 100,
              useNativeDriver: true
            }),
            Animated.timing(flashOpacity, {
              toValue: 0,
              duration: 100,
              useNativeDriver: true
            })
          ]).start();

          // Delay capture callback to allow flash to be visible
          setTimeout(() => {
            onCapture(imageSrc);
          }, 100);
        }
      }
    };

    // Use addEventListener for web-specific click handling
    const containerElement = containerRef.current as unknown as HTMLDivElement;
    containerElement?.addEventListener('click', handleClick);

    return () => {
      containerElement?.removeEventListener('click', handleClick);
    };
  }, []);

  return (
    <View 
      ref={containerRef}
      style={styles.container}
    >
      <Webcam
        audio={false}
        ref={webcamRef}
        screenshotFormat="image/jpeg"
        videoConstraints={{
          facingMode: "environment",
          width: { ideal: Dimensions.get('window').height },
          height: { ideal: Dimensions.get('window').width }
        }}
        style={styles.webcam}
      />
      <Text style={styles.tapCountText}>Taps: {tapCount}</Text>

      <Animated.View 
        style={[
          styles.flashOverlay, 
          { 
            opacity: flashOpacity,
          }
        ]} 
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    position: 'relative',
  },
  webcam: {
    width: '100%',
    height: '100%',
    objectFit: 'cover' as any,
  },
  flashOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'white',
    zIndex: 10,
  },
  tapCountText: {
    position: 'absolute',
    top: 20,
    left: 20,
    color: 'white',
    backgroundColor: 'rgba(0,0,0,0.5)',
    padding: 10,
  }
});

export default WebCameraCapture;
