import React, { useRef, useState } from 'react';
import { View, TouchableWithoutFeedback, Animated, StyleSheet, Dimensions } from 'react-native';
import Webcam from 'react-webcam';

interface WebCameraCaptureProps {
  onCapture: (image: string) => void;
}

const WebCameraCapture: React.FC<WebCameraCaptureProps> = ({ onCapture }) => {
  const webcamRef = useRef<Webcam>(null);
  const flashOpacity = useRef(new Animated.Value(0)).current;

  const captureImage = () => {
    console.log(32432)
    if (webcamRef.current) {
      const imageSrc = webcamRef.current.getScreenshot();
      if (imageSrc) {
        // Trigger flash animation
        Animated.sequence([
          Animated.timing(flashOpacity, {
            toValue: 0.7,  // Reduced opacity to keep camera visible
            duration: 100,
            useNativeDriver: true
          }),
          Animated.timing(flashOpacity, {
            toValue: 0,
            duration: 100,
            useNativeDriver: true
          })
        ]).start();

        // Delay capture callback to allow flash to be visible
        setTimeout(() => {
          onCapture(imageSrc);
        }, 100);
      }
    }
  };

  return (
    <View style={styles.container}>
      <TouchableWithoutFeedback onPress={captureImage}>
        <View style={styles.cameraContainer}>
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            videoConstraints={{
              facingMode: "environment",
              width: { ideal: Dimensions.get('window').height },
              height: { ideal: Dimensions.get('window').width }
            }}
            style={styles.webcam}
          />
        </View>
      </TouchableWithoutFeedback>

      <Animated.View 
        style={[
          styles.flashOverlay, 
          { 
            opacity: flashOpacity,
          }
        ]} 
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    position: 'relative',
  },
  cameraContainer: {
    flex: 1,
    overflow: 'hidden',
  },
  webcam: {
    width: '100%',
    height: '100%',
    objectFit: 'cover' as any,
  },
  flashOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'white',
    zIndex: 10,
  },
});

export default WebCameraCapture;
